import rclpy
from rclpy.node import Node

class MioNodo(Node):
    def __init__(self):
        super().__init__('nome_del_nodo')
        self.get_logger().info('Ciao! Il nodo Ã¨ partito e ROS2 sta funzionando.')

def main(args=None):
    rclpy.init(args=args)
    nodo = MioNodo()
    rclpy.spin(nodo)
    rclpy.shutdown()

if __name__ == '__main__':
    main()